const Counter = ({ count, setCount }) => {
  const substraction = () => {
    if (count > 0) setCount(count - 1);
  };

  const addition = () => {
    setCount(count + 1);
  };

  return (
    <div className="counter">
      <button onClick={substraction}>Remove 1</button>
      <div id="count">{count}</div>
      <button onClick={addition}>Add 1</button>
    </div>
  );
};

export default Counter;
